-- ----------------------------
DROP TABLE IF EXISTS `organization`;
CREATE TABLE `organization` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键自增',
  `name` varchar(50) DEFAULT NULL COMMENT '机构名',
  `intro` text COMMENT '单位简介',
