-- ----------------------------
-- Records of video_news
-- ----------------------------
INSERT INTO `video_news` VALUES ('15', '解读十九大报告七大战略之乡村振兴战略', '习近平同志在十九大报告中首度提出，“实施乡村振兴战略”是新时期做好“三农”工作的重要遵循。实施乡村振兴战略，是立足于社会主义初级阶段基本国情，着眼于确保如期全面建成小康社会和基本实现现代化、实现国家长治久安作出的重大决策部署。实施好这一战略，必须科学制定规划，强化制度供给，统筹推进农村经济建设、政治建设、文化建设、社会建设、生态文明建设，加快推进农业农村现代化，让亿万农民过上更加幸福美好的新生活。', 'http://47.104.8.66/video/2017-12-11/6916b2e802d841e6a663df7ed6c5b831.mp4', '2017-12-07 20:34:31', '0', '17');
INSERT INTO `video_news` VALUES ('16', '邵维正：如何把握新时代我国社会的主要矛盾', '党的十九大在作出中国特色社会主义进入了新时代历史性判断的同时,明确指出:“我国社会主要矛盾已经转化为人民日益增长的美好生活需要和不平衡不充分的发展之间的矛盾。”这个论断对于未来决胜全面建成小康社会,实现社会主义现代化和中华民族伟大复兴,都具有十分深远的意义。国防大学联合勤务学院教授邵维正作客中国社会科学网，为我们解读如何把握新时代我国社会的主要矛盾。', 'http://47.104.8.66/video/2017-12-11/eaee2a46a4de444c820cd653b556021a.mp4', '2017-12-10 21:52:36', '0', '17');

-- ----------------------------
-- Table structure for zk_expert
-- ----------------------------
DROP TABLE IF EXISTS `zk_expert`;
CREATE TABLE `zk_expert` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键自增',
  `img` varchar(255) DEFAULT NULL COMMENT '专家照片',
  `name` varchar(50) DEFAULT NULL COMMENT '专家姓名',
  `position` varchar(50) DEFAULT NULL COMMENT '专家职位',
  `organizationId` int(11) DEFAULT NULL COMMENT '所属机构',
  `organizationName` varchar(50) DEFAULT NULL COMMENT '机构名',
  `resume` text COMMENT '个人简历',
  `research` varchar(255) DEFAULT NULL COMMENT '研究领域',
  `fieldType` int(11) DEFAULT '0' COMMENT '研究领域类型0党建、1 社会、2生态、3政治、4经济、5文化、6热点专题、7国际关系',
  `educational` varchar(255) DEFAULT NULL COMMENT '教育背景',
  `report` text COMMENT '内部报告',
  `academicPaper` text COMMENT '学术论文',
  `composition` text COMMENT '学术著作',
  `researchProject` varchar(255) DEFAULT NULL COMMENT '研究项目',
  `partTimeJob` varchar(255) DEFAULT NULL COMMENT '学术兼职',
  `socialPrestige` varchar(255) DEFAULT NULL COMMENT '社会荣誉',
  `researchResult` text COMMENT '研究成果',
  `contactWay` varchar(50) DEFAULT NULL COMMENT '联系方式',
  `shield` int(11) DEFAULT '0' COMMENT '0(不屏蔽), 1(屏蔽)',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=33 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

-- ----------------------------
-- Records of zk_expert
-- ----------------------------
INSERT INTO `zk_expert` VALUES ('23', 'http://47.104.8.66/img/2017-12-27/3b7f13ef98f6450dade622be16266aa0.jpg', '夏振坤', null, '49', '湖北省社会科学院', '夏振坤，男，1928年2月14日出生于庐山北麓的江西省九江市莲溪乡。1947年由南昌豫章中学考入湖北农学院农经系，1953年毕业于中国人民大学研究生班。毕业后任华中农学院讲师、高级经济师。1984年春调湖北省社会科学院，历任副院长、院长、党组书记、研究员之职。现任湖北省社科院研究员、华中理工大学教授及博士生导师。曾任中国社会科学院及武汉大学经济学院博士生导师、中国生产力经济学会、中国区域经济学会常务理事，中国宏观经济学会理事，中共湖北省委财经领导小组顾问，湖北经济学团体联合会执行主席，湖北高级专家协会副会长。1991年授予国务院特贴专家称号。', null, '4', null, null, null, '已出版学术著作《中国农业发展模式探讨》、《论改革与发展》、《中国的改革与农业发展》、《绿色革命之路：大国农业发展理论与模式》、《社会主义与改革的理论探索》、《发展经济学新探》、《中国跨世纪的再就业工程研究》、《发展经济学概论》、《时代潮流中的中国现代化》等19部（独著、合著、主编），在《人民日报》、《光明日报》、《中国社会科学》、《新华文摘》、《经济研究》、《江汉论坛》等报刊发表论文200余篇', null, null, null, '在农业经济学、发展经济学、社会主义改革与发展诸领域均有突破和建树。许多研究成果获得了很高的社会荣誉，如全国首届“五个一工程”奖，湖北省社会科学优秀成果一等奖等。', null, '0');
INSERT INTO `zk_expert` VALUES ('24', 'http://47.104.8.66/img/2017-12-27/05f4e21d50c44c888c86e81c01431ac1.jpg', '曾成贵', null, '49', '湖北省社会科学院', '曾成贵，1954年生，湖北浠水人，历史学博士，研究员，湖北省有突出贡献的中青年专家，享受国务院政府特殊津贴，现任政协湖北省第十一届委员会常委、省政协文史和学习委员会副主任，省第十次党代会代表；历任湖北省社会科学院历史研究所副所长、所长，院党组成员、副院长，院长、党组副书记，党组书记、副院长，省社科联第七届副主席，省第十一届人大代表、省人大法制委员会委员；社会兼职现有：中国近现代史史料学学会副会长、湖北省中共党史学会副会长、湖北省中共党史人物研究会副会长、湖北省新四军研究会副会长等。主要从事中共党史和中国近现代政治史研究，有代表性的著作十种、论文百篇。', null, '3', null, null, null, null, null, null, null, null, null, '0');
INSERT INTO `zk_expert` VALUES ('25', 'http://47.104.8.66/img/2017-12-27/fa5c29697d5d4942af5962a2591f2528.jpg', '刘玉堂 ', null, '49', '湖北省社会科学院', '刘玉堂，男，1956年生，湖北大悟人。先后毕业于湖北大学、华中师范大学、武汉大学，分获史学学士、硕士、博士学位。现任湖北省社会科学院副院长、研究员、博士生导师，华中科技大学、武汉理工大学、北京工业大学、湖北工业大学、长江大学等高校兼职教授。社会兼职主要有中国民族史学会副会长、中国建筑园林研究会常务理事、湖北省楚国历史文化学会会长、湖北省三国文化研究会会长、湖北省荆楚文化研究会副会长、湖北省炎黄文化研究会副会长等。1997年获“湖北省有突出贡献中青年专家”称号，2002年入选“湖北省新世纪高层次人才”，2005年入选“中央直接掌握联系的高层次人才”和湖北省宣传文化战线首届“五个一批”人才。', '长期致力于中国经济史、中国法制史和中国城市史研究', '4', '先后毕业于湖北大学、华中师范大学、武汉大学，分获史学学士、硕士、博士学位', null, null, '其代表性著作《楚国经济史》（湖北教育出版社1995年出版）', null, null, null, null, null, '0');
INSERT INTO `zk_expert` VALUES ('26', 'http://47.104.8.66/img/2017-12-27/a4a0fa7e534d4b4697d3be61ece9ec4e.jpg', '邹进泰', null, '49', '湖北省社会科学院', '邹进泰，男，1961年7月生，湖北松滋人，研究生学历，经济学硕士，湖北省有突出贡献中青年专家。1985年毕业于华中农业大学农业经济系。1985年，在农业部管理干部学院任教。1987年起，历任湖北省社会科学院助理研究员、副研究员、研究员等。1998年至今任湖北省社会科学院农村经济研究所所长、湖北省社会科学院产业经济学硕士点导师组长。主要社会兼职（学术）有：湖北省农村经济学会副理事长、湖北省软科学研究会副理事长、湖北省畜牧业经济学会副理事长 湖北省经济学团体联合会常务理事等。', '邹进泰研究员长期从事三农问题研究，同时在发展经济学、产业经济学、数量与技术经济学等学科也有广泛涉猎。', '4', null, null, null, null, null, null, null, '获奖情况：《农业科技成果经济效益评价》获湖北省科技进步三等奖（１９９３年）、《中国政府消除贫困行为》获湖北省五个一工程奖、《绿色经济》获湖北省2004年社会科学成果三等奖、《中国农产品比较优势研究与应用》获2006年湖北省科学技术进步二等奖。', null, '0');
INSERT INTO `zk_expert` VALUES ('27', 'http://47.104.8.66/img/2017-12-27/3a926eb860f04005bdc22716122ace05.jpg', '秦尊文', null, '49', '湖北省社会科学院', '秦尊文，男，1961年7月生，湖北荆门人，中共党员，中南财经政法大学区域经济学专业博士研究生毕业，获经济学博士学位，研究员，博士研究生导师。现任湖北省社会科学院副院长，长江中游城市群研究中心主任，赛马彩票研究中心主任。系科技部特聘专家，湖北省委党内法规专家，湖北省人大立法顾问，湖北省政府咨询委员，湖北省政府学位委员会学科评议组成员，湖北省经济学界团体联合会副主席，湖北省区域经济学会会长，中国区域经济学会、中国区域科学协会、中国城市经济学会常务理事，中共湖北省委党校、中南财经政法大学、中国地质大学（武汉）、中南民族大学、武汉科技大学等单位的兼职教授（博导）、客座研究员、学科建设顾问、特聘专家，武汉城市圈商会专家委员会主席，武汉市政府决策咨询委员，襄阳市委决策支持顾问，宜昌市政府咨询委员，黄石市政府咨询委员，十堰市委、市政府生态文明建设专家顾问。', null, '0', null, null, null, '共发表学术论文、调研报告500多篇，出版著作20多部，主持和参与课题研究数十项', '秦尊文是湖北省重点学科“应用经济学”负责人，主持过一系列区域规划编制和区域发展战略重大课题研究，参与过湖北省十五至十三五规划编制研究（为近几个五年规划专家委员会成员）', null, null, '科研成果获得湖北省委、省政府授奖9项，其中一等奖2项，二等奖4项。2008年应美国国务院邀请，作为高级学者访问美国考察区域发展、城市群和赛马产业。是国家批准的《长江中游城市群发展规划》、《武汉市江汉区服务业改革试点方案》、《湖北荆州承接产业转移示范区方案》和湖北省政府批准的《湖北长江经济带开放开发总体规划》、《湖北汉江生态经济带开放开发总体规划》、《湖北小池滨江新区开放开发总体规划》、《富强湖北建设实施方案》编制专家组组长。', null, '0');
INSERT INTO `zk_expert` VALUES ('28', 'http://47.104.8.66/img/2017-12-27/23c68f41bcf84af48a56de16da54fbda.jpg', '陈金清', '副会长', '49', '湖北省社会科学院', '陈金清，男，1962年5月出生，1979年进入武汉大学哲学系学习，1983年7月毕业后被分配到武汉工学院院长办公室从事文字秘书工作，1985年9月考入湖北省社会科学院哲学专业攻读硕士研究生，1988年7月毕业后被分配到江汉论坛杂志社从事哲学编辑和研究工作。1989年获助理研究员职称、1994年获副研究员职称、2001年获研究员职称，2005年被中南财经政法大学聘为兼职教授。1997年担任《江汉论坛》副主编、次年任副社长兼副主编、2010年任社长、2013年担任社长兼常务副主编。目前担任的学术团体职务有：湖北省哲学学会副会长、湖北省社科期刊研究会副会长、湖北省向阳湖文化研究会荣誉副会长。', null, '0', null, null, null, '在《光明日报》、《中国社会科学报》、《武汉大学学报》、《学习与探索》等报刊发表论文100余篇，主编及参与撰写著作15部。', null, null, '在从事编辑和研究工作过程中，1997年获湖北省社科院“十佳科研人员”称号；2003年获湖北省新闻出版局授予的“全省期刊优秀编辑”称号；2013年获湖北省新闻出版局授予的“优秀期刊社长”称号。', '2009年参加撰写的《湖北的文化优势和文化强省战略研究》获湖北省人民政府颁发的“湖北发展研究奖三等奖”；主持完成的《推进社会主义核心价值体系大众化的基本路径》获2011年度全省宣传思想文化工作优秀调研报告奖。', null, '0');
INSERT INTO `zk_expert` VALUES ('29', 'http://47.104.8.66/img/2017-12-28/a4383daa850a4090a38f43d01372aa62.png', '张忠家', '党组书记', '49', '湖北省社会科学院', '张忠家，1963年8月生，湖北大悟人，1984年毕业于武汉师范学院（现湖北大学）孝感分院中文系，2001年6月获华中师范大学行政管理专业硕士学位，2005年6月获武汉理工大学管理科学与工程专业博士学位。现为武汉理工大学管理学博士生导师、长江大学硕士生导师。2003年8月任长江大学党委常委、书记，湖北省高校党务工作研究会副会长，湖北省高校管理研究会副理事长。2013年6月任湖北省社会科学院党组书记、副院长 。曾获全国优秀教师，湖北省高校优秀党务工作者等荣誉称号。', '张忠家教授，长期从事科技与教育管理、教育经济与管理和高等教育管理的研究生教学工作，主要研究方向是：（1）科技与教育管理；（2）教育经济与管理；（3）高等教育管理的理论与实践。先后组织编写了《多维视角的和谐社会研究》、《教育经济配置效率研究》、《跨世纪高校德育工作研究》等教材和专著10余部，公开发表论文40余篇，主持和参加国家社科基金项目、湖北省社科基金重点项目、中国高教学会“十一五”教育科学研究重点规划项目、湖北省教育厅社科项目15项。', '0', null, null, null, null, null, null, null, '其主持的《优化大学教育资源配置的战略思考》获湖北省社会科学成果三等奖，参与的《产学研与产学研合作教育研究》获得湖北省优秀教学成果一等奖。 ', null, '0');
INSERT INTO `zk_expert` VALUES ('30', 'http://47.104.8.66/img/2017-12-28/caf887eeed7f45ec809cdb0e7907b185.png', '宋亚平', '院长', '49', '湖北省社会科学院', '宋亚平，湖北省社会科学院院长、党组副书记、研究员，历史学博士。兼任华中师范大学政治学研究院教授、博士生导师。  曾长期在市（县）及省直党政部门工作，先后担任过枣阳市常务副市长、咸宁市咸安区委书记、省委政研室副主任等职。  多年从事政治学特别是关于地方政府学方面的研究，是省内外知名的“三农”问题研究专家。  出版  《湖北地方政府与经济建设》  《中国早期现代化研究》  《咸安政改》  《出路》  《湖北新型城镇化转型与治理研究》等专著。  主编《湖北经济社会发展年度报告》。  科研成果多次获国家部委奖、省委调研成果奖、省政府发展研究奖等。国家发改委、北京大学“百年讲坛”和上海、山东、河南、安徽、湖南等省、市委中心学习组多次邀请宋亚平同志作改革及“三农”方面的专题报告，《凤凰周刊》等媒体多次将宋亚平同志评为“年度全国新闻人物”。  2008年当选“中国改革开放30年风云人物”、“改革开放30年，影响湖北30人”。  2008年，宋亚平担任湖北省社会科学院院长以来，根据省委、省政府提出的“进一步办好社科院”“努力实现打造新型智库的目标”的总要求，坚持人才强院、学术立院、改革兴院、制度治院，积极推进社科院发挥马克思主义宣传的坚强阵地，哲学社会科学研究的学术殿堂，服务于地方党委政府科学决策的专门智库，全国硕士研究生教育的培养基地职能，取得了极其丰硕的成果，为推进马克思主义中国化、繁荣哲学社会科学、服务“建成支点、走在前列”战略的实施、谱写中国梦的湖北篇提供智力支持和精神动力。', null, '0', null, null, null, null, null, null, null, null, null, '0');
INSERT INTO `zk_expert` VALUES ('31', 'http://47.104.8.66/img/2017-12-28/1c1a95aca566482c96dc1b5a656f9939.jpg', '杨述明', '副院长', '49', '湖北省社会科学院', '杨述明，男，1961年11月出生，1981年7月参加工作，1985年6月加入中国共产党党，武汉大学博士研究生、经济学博士。历任襄阳市保康县成人中专学校副校长、校长；保康县茶叶办公室主任；绿生集团董事长、党委书记；保康县委、县政府办公室副主任；保康县寺坪镇党委书记、人大主席；保康县委常委、组织部长，期间被选派赴美培训学习；枣阳市委副书记；樊城区委副书记、区长;襄阳市樊城区委书记、区人大主任；襄阳市委副秘书长；现任湖北省社会科学院党组成员、副院长。', null, '0', null, null, null, null, null, null, null, null, null, '0');
INSERT INTO `zk_expert` VALUES ('32', 'http://47.104.8.66/img/2017-12-28/e92ab4f5d6ca455a8d17e10f18b4ebb0.jpg', '魏登才', '秘书长', '49', '湖北省社会科学院', '魏登才：男，汉族，湖北监利人，中共党员。1966年9月出生，大学学历，副研究员，硕士研究生导师。  1988年7月毕业于山东大学科学社会主义系，同年被分配到湖北省社会科学院从事科研工作，先后在政治学所、法学所从事政治学、法学理论研究和《法治时代》编辑工作。1990年10月调入院机关工作，先后任院团委专职副书记、政治处主任科员。1997年3月，任人事处副处长兼院团委书记；2000年12月至2002年12月，下派咸宁市咸安区任副区长（挂职）；2006年1月，任院机关党委专职副书记兼工会主席；2007年7月，任院办公室主任，兼任机关党委专职副书记、工会主席、《要文摘报》副主编；2010年1月专任院办公室主任、《要文摘报》副主编。2015年5月，任院秘书长、党组成员、《要文摘报》副主编。  曾被湖北省委授予全省“优秀党务工作者”称号，被湖北省直机关工委授予十佳“模范党务工作者”“优秀党务工作者”“优秀共产党员”称号。  在做好行政工作的同时，长期兼做科研工作，取得了一定成绩。1999年9月被聘为副研究员，2003年9月被遴选为省社科院硕士研究生指导老师。是湖北省第九届青年联合会委员，现兼任湖北省机关党建研究会常务理事、湖北省直机关思想政治工作研究会常务理事、湖北省延安精神研究会理事等职。  主要从事政治学、党建、人才学等学科研究。发表论文有多篇被中国人民大学书报资料全文转载，有多篇被《中国新时期人文科学优秀成果精选》等大型文献类工具书收录。执笔撰写的咨询建议类文章，有的通过省委办公厅报送到中央有关部门，有的获省委省政府主要领导批示并转发，有的被省委省政府有关部门采用。先后主持湖北省重大调研课题基金课题1项，参与国家社科基金课题研究2项，作为执笔人完成省社科基金课题研究2项，省软科学课题研究2项。科研成果获湖北省优秀社科成果奖2项，省委决策支持奖1项，获地厅级优秀调研成果奖10余项。', null, '0', null, null, null, null, null, null, null, null, null, '0');

-- ----------------------------
-- Function structure for GET_FIRST_PINYIN_CHAR
-- ----------------------------
DROP FUNCTION IF EXISTS `GET_FIRST_PINYIN_CHAR`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` FUNCTION `GET_FIRST_PINYIN_CHAR`(PARAM VARCHAR(255)) RETURNS varchar(2) CHARSET utf8
BEGIN  
    DECLARE V_RETURN VARCHAR(255);  
    DECLARE V_FIRST_CHAR VARCHAR(2);  
    SET V_FIRST_CHAR = UPPER(LEFT(PARAM,1));  
    SET V_RETURN = V_FIRST_CHAR;  
    IF LENGTH( V_FIRST_CHAR) <> CHARACTER_LENGTH( V_FIRST_CHAR ) THEN  
    SET V_RETURN = ELT(INTERVAL(CONV(HEX(LEFT(CONVERT(PARAM USING gbk),1)),16,10),  
        0xB0A1,0xB0C5,0xB2C1,0xB4EE,0xB6EA,0xB7A2,0xB8C1,0xB9FE,0xBBF7,  
        0xBFA6,0xC0AC,0xC2E8,0xC4C3,0xC5B6,0xC5BE,0xC6DA,0xC8BB,  
        0xC8F6,0xCBFA,0xCDDA,0xCEF4,0xD1B9,0xD4D1),  
    'A','B','C','D','E','F','G','H','J','K','L','M','N','O','P','Q','R','S','T','W','X','Y','Z');  
    END IF;  
    RETURN V_RETURN;  
END
;;
DELIMITER ;
